import 'package:flame/components.dart';

import 'country_data.dart';

/// Available game regions.
enum GameRegion {
  world,
  usStates,
  ukCounties,
  caribbean,
  ireland,
  canadianProvinces,
}

/// Whether a region uses a flat map projection instead of the globe.
///
/// Flat-map regions show a static satellite view with the plane moving
/// across the screen, rather than the globe-centred view.
bool isRegionalFlatMap(GameRegion region) {
  switch (region) {
    case GameRegion.world:
    case GameRegion.caribbean:
      return false;
    case GameRegion.usStates:
    case GameRegion.ukCounties:
    case GameRegion.ireland:
    case GameRegion.canadianProvinces:
      return true;
  }
}

extension GameRegionExtension on GameRegion {
  String get displayName {
    switch (this) {
      case GameRegion.world:
        return 'World';
      case GameRegion.usStates:
        return 'US States';
      case GameRegion.ukCounties:
        return 'British Counties';
      case GameRegion.caribbean:
        return 'Caribbean';
      case GameRegion.ireland:
        return 'Ireland';
      case GameRegion.canadianProvinces:
        return 'Canada';
    }
  }

  String get description {
    switch (this) {
      case GameRegion.world:
        return 'Navigate the entire globe';
      case GameRegion.usStates:
        return 'Explore all 50 US states';
      case GameRegion.ukCounties:
        return 'Discover counties of the UK';
      case GameRegion.caribbean:
        return 'Island hop through the Caribbean';
      case GameRegion.ireland:
        return 'Tour all 32 counties of Ireland';
      case GameRegion.canadianProvinces:
        return 'Fly across Canada\'s provinces';
    }
  }

  /// Map bounds for the region [minLng, minLat, maxLng, maxLat]
  List<double> get bounds {
    switch (this) {
      case GameRegion.world:
        return [-180, -85, 180, 85];
      case GameRegion.usStates:
        return [-125, 24, -66, 50];
      case GameRegion.ukCounties:
        return [-11, 49, 3, 61];
      case GameRegion.caribbean:
        return [-85, 10, -59, 28];
      case GameRegion.ireland:
        return [-11, 51, -5, 56];
      case GameRegion.canadianProvinces:
        return [-141, 42, -52, 72];
    }
  }

  /// Center point for the region
  Vector2 get center {
    final b = bounds;
    return Vector2((b[0] + b[2]) / 2, (b[1] + b[3]) / 2);
  }

  /// Required level to unlock this region
  int get requiredLevel {
    switch (this) {
      case GameRegion.world:
        return 1;
      case GameRegion.usStates:
        return 3;
      case GameRegion.ukCounties:
        return 5;
      case GameRegion.caribbean:
        return 7;
      case GameRegion.ireland:
        return 10;
      case GameRegion.canadianProvinces:
        return 4;
    }
  }

  /// Whether this region uses a flat map projection instead of the globe.
  bool get isFlatMap => isRegionalFlatMap(this);
}

/// Regional area shape (state, county, island, etc.)
class RegionalArea {
  const RegionalArea({
    required this.code,
    required this.name,
    required this.points,
    this.capital,
    this.population,
    this.funFact,
  });

  final String code;
  final String name;
  final List<Vector2> points;
  final String? capital;
  final int? population;
  final String? funFact;
}

/// Regional data provider.
abstract class RegionalData {
  /// Get areas for a region
  static List<RegionalArea> getAreas(GameRegion region) {
    switch (region) {
      case GameRegion.world:
        return CountryData.countries
            .map((c) => RegionalArea(
                  code: c.code,
                  name: c.name,
                  points: c.allPoints,
                  capital: c.capital,
                ))
            .toList();
      case GameRegion.usStates:
        return _usStates;
      case GameRegion.ukCounties:
        return _ukCounties;
      case GameRegion.caribbean:
        return _caribbeanIslands;
      case GameRegion.ireland:
        return _irelandCounties;
      case GameRegion.canadianProvinces:
        return _canadianProvinces;
    }
  }

  /// Get random area from a region
  static RegionalArea getRandomArea(GameRegion region) {
    final areas = getAreas(region);
    return areas[DateTime.now().millisecondsSinceEpoch % areas.length];
  }

